// models/RoomDetails.js
const mongoose = require('mongoose');

const Schema = mongoose.Schema;

const roomDetailsSchema = new Schema({

    // Room ID (Auto Generated)
    roomId: {
        type: String,
        unique: true
    },

    // Basic Information
    roomNumber: {
        type: String,
        required: true,
        trim: true
    },

    monthlyPrice: {
        type: Number,
        required: true
    },

    roomType: {
        type: String,
        required: true,
        enum: ['single', 'shared']
    },

    maxOccupancy: {
        type: Number,
        required: true
    },

    floorNumber: {
        type: Number,
        required: true
    },

    size: {
        type: Number, // sq.ft
        required: true
    },

    description: {
        type: String,
        trim: true
    },

    // Availability
    availableFrom: {
        type: Date
    },

    availableTo: {
        type: Date
    },

    // Room Status
    status: {
        type: String,
        required: true,
        enum: ['available', 'occupied', 'maintenance', 'unavailable'],
        default: 'available'
    },

    // Amenities
    amenities: {
        privateBathroom: { type: Boolean, default: false },
        airConditioning: { type: Boolean, default: false },
        highSpeedWifi: { type: Boolean, default: false },
        studyDesks: { type: Number, default: 0 },
        storageLockers: { type: Boolean, default: false },
        miniFridge: { type: Boolean, default: false },
        tv: { type: Boolean, default: false },
        balcony: { type: Boolean, default: false },
        microwave: { type: Boolean, default: false },
        washingMachine: { type: Boolean, default: false },
        waterHeater: { type: Boolean, default: false },
        parking: { type: Boolean, default: false }
    },

    // Images (store image URLs or file paths)
    images: [
        {
            type: String
        }
    ]

}, { 
    timestamps: true  // createdAt & updatedAt auto generate වෙනවා
});


// Room ID Generator
function generateRoomId() {
    const timestamp = Date.now().toString().slice(-6);
    return 'RM-' + timestamp;
}


// Save කරන්න කලින් Room ID generate කරන්න
roomDetailsSchema.pre('save', function(next) {
    if (!this.roomId) {
        this.roomId = generateRoomId();
    }
    next();
});


// Create Model
const RoomDetails = mongoose.model('RoomDetails', roomDetailsSchema);

module.exports = RoomDetails;